import { createSelector } from '@reduxjs/toolkit';
import { sort } from '../../utils/array';
export const selectCategories = (state) => state.categories.categories;
export const selectCategoriesLoaded = (state) => state.categories.categoriesLoaded;
export const selectCategoriesError = (state) => state.categories.categoriesError;
export const selectCategoryBreakdown = (state) => state.categories.categoryBreakdown;
export const selectSelectedCategoryBreakdown = (state) => state.categories.selectedCategoryBreakdown;
export const selectBreakdownStart = (state) => state.categories.breakdownStart;
export const selectBreakdownEnd = (state) => state.categories.breakdownEnd;
export const selectSelectedCategory = (state) => state.categories.selectedCategory;
export const selectSelectedAccounts = (state) => state.categories.selectedAccounts;
export const selectCategoryBreakdownLoaded = (state) => state.categories.categoryBreakdownLoaded;
export const selectSelectedCategoryBreakdownLoaded = (state) => state.categories.selectedCategoryBreakdownLoaded;
export const selectSelectedCategoryActivity = (state) => state.categories.selectedCategoryActivity;
export const selectSortedSelectedCategoryActivity = createSelector(selectSelectedCategoryActivity, (activity) => sort(activity, (a, b) => a.category === b.category ? a.amount - b.amount : a.category.localeCompare(b.category)));
